mapper.select:
SELECT NOTICE_NO, TITLE, CONTENT, HIT, CREATED, LASTMODIFIED
  FROM NOTICE
 WHERE NOTICE_NO = #{noticeNo}

mapper.update:
UPDATE NOTICE
   SET HIT = HIT + 1
 WHERE NOTICE_NO = #{noticeNo}

UPDATE NOTICE
   SET TITLE = #{title}
     , CONTENT = #{content}
     , LASTMODIFIED = SYSTIMESTAMP
 WHERE NOTICE_NO = #{noticeNo}
